css:
@-moz-document domain(community.wanikani.com) {

.like-button:hover {
    background: transparent !important;
}

.like-count {
    font-size: 1em !important;
    margin-right: 2px !important;
}

.like-count[title*="1 person"]:after,
.like-count[title*="you liked this post"]:after {
    content: ' Like';
}

.like-count:after {
    content: ' Likes';
}

.toggle-like {
    padding: 8px 10px !important;
}

.my-likes i {
    display: none !important;
}

.fa-heart-o:before {
    content: "ï€„";
}

nav.post-controls .like-button:active {
    box-shadow: none;
}
}
