css:
@-moz-document regexp('^https://([a-zA-Z0-9][-_a-zA-Z0-9]+)\.github\.io.*') {


body {
        background-color:  gray;   
        color:  #004b96;
        /*display: flex;*/
        justify-content: center;
    }

    input, textarea {
        background-color: darkgray;   
        color:  chocolate;
        margin: 8px;
        max-width: 800px;
    }

    #push {
        margin-left: 0px !Important;
        min-width: 600px;   
    }

    button {
        background-color:  green;
        color: white;
        border-radius:  10px;
        border-style: outset;
        border-color: darkgreen;
        display: block;
        height: 25px;
        width: 100px;
    }

    #pushbtn :before {
        content:  '\A';   
    }

    #console {
        width: 800px;
        overflow-x: hidden;
        margin-top:  50px;
        border-width: 1px;
        border-color: gray;
        border-style: solid;
        border-radius: 8px;
        padding: 10px;
    }

    #console > p {
        width: 800px;
    }

    #console > p > i {
        width: 800px;
        font-style: normal;
    }

    #console :-webkit-scrollbar-track {
        background-color:  chocolate;
        padding: 5px;
    }

    #console :-webkit-scrollbar-thumb {
        background:  blue;
    }
	pre .string, pre .support.value {
 		color: #00A !important;                                            
 		color: #33A !important;                                            
	}
	pre .entity.function, pre .entity.id {
 		color: #00A !important;                                            
    }                                       
    pre {
        display: block;
        /*background: #759Ac4 !important;*/
        background: #BBB !important;
    }
    pre code[class^='language-go'] {
        /*background: #759Ac4 !important;*/
        background: #BBB !important;
    }

    pre code[class^='language-json'] {
        /*background: #759Ac4 !important;*/
        background: #BBB !important;
    }
}
