css:
@-moz-document domain("cryptocompare.com") {
  .navbar-main, .portfolio-ad > a, .md-ad > img, .md-ad > span, .footer, .portfolio-explanation, .portfolio-caution, .md-faketabs.faketabs-chart, .secondary-stats, .chart-portfolio-widget, .widgets-container, .change-indicator .glyphicon-chevron-up, .change-indicator .glyphicon-chevron-down {
      display: none !important;
  }

  .lights-off .current-change-currency, .lights-off .current-price-value {
      border: 1px solid #191b1f !important;
  }

  .text-small {
      font-size: 14px !important;
      line-height: 15px !important;
  }

  .current-change-currency {
      padding-right: 0px !important;
  }

  .current-price-change-value {
      font-size: 14px !important;
      padding-left: 15px !important;

  }

  .portfolio-member-details {
      font-size: 14px !important;
  }

  .col-price {
      text-align: right !important;
  }

  .md-button.md-raised:not([disabled]) {
      box-shadow: none !important;
  }

  .portfolio-actions > button {
    background: transparent !important;
  }
}
