css:
@-moz-document domain("github.com"), domain("gist.github.com") {
	/* operator mono */
  .blob-code-inner,
  .blob-num,
  .highlight pre,
  .diff-table,
  .tab-size {
      font-family: "Ofirator Nerd Font", "Operator Mono Lig", "Operator Mono", "Fira Code", monospace !important;
      font-size: 14px !important;
      font-weight: 200;
      tab-size: 4;
  }

  .pl-c,
  .pl-e,
  .pl-ii {
      font-style: italic;
  }

  .pl-c,
  .pl-ii {
      color: #4CAF50;
      background-color: inherit;
  }

  /* code wrap */
  .blob-code-inner,
  .markdown-body pre > code,
  .markdown-body .highlight > pre {
      white-space: pre-wrap !important;
      word-break: break-all !important;
      overflow-wrap: break-word !important;
      display: block !important;
  }

  td.blob-code-inner {
      display: table-cell !important;
  }

  /* hanging indent */
  /* .blob-code,
  .blob-code-inner,
  .js-file-line {
      padding-left: 8ch;
      text-indent: -8ch
  } */
}
