@-moz-document domain("gorillatoolkit.org") {
    /* Insert code here... */
    body {
        background: inherit;
        color: inherit;
    }
    code, pre {
        border: 1px solid hsl(0, 0%, 80%);
        border-radius: 5px;
           color: hsl(0, 0%, 10%);
    background: #f2f2f2;
        background-image: none;
    }
    a:link, a:visited {
        color: hsl(215, 100%, 50%);
        text-decoration: none;
    }
    #gorilladoc h2, #gorilladoc h3, #gorilladoc h4, #gorilladoc h5, #gorilladoc h6 {
        border-top: 1px solid #333;
        font-family: "Consolas", "Deja Vu Sans Mono", "Lucida Console", "Bitstream Vera Sans Mono", monospace;
        padding-top: 16px;
        text-shadow: none;
    }
    #nav table, #nav, footer, header nav ul {
        border: none;
        background: inherit;
    }
    .main h1, .main h2, .main h3, .main h4 {
        font-family: helvetica;
        color: inherit;
        text-shadow: none;
    }
    header nav a:hover, header nav a:active, header nav li.active a, header nav a:link, header nav a:visited {
        font-size: 1.3em;
        color: inherit;
        background: inherit;
        background-image: inherit;
    }
    .main h2 {
        border-top: 1px solid #333;
    }
}
