css:
@-moz-document url-prefix("https://platinumcheats.net/config/user"), url-prefix("https://platinumcheats.net/config/keybind"), url-prefix("https://platinumcheats.net/config/inventory"), url-prefix("https://platinumcheats.net/config/view/"), url-prefix("https://platinumcheats.net/config/user"), url-prefix("https://platinumcheats.net/config/inventory") {
Body {
    background: #202020;
}

.ui.vertical.menu {
    background: #202020;
}

.ui.icon.menu .item {
    color: #fff;
}

.ui.segment {
    background: #fff;
}

.ui.tabular.menu .active.item {
    background: #202020;
    color: #fff;
}

.ui.tabular.menu .item:hover {
    color: #fff;
}

.ui.tabular.menu + .attached:not(.top).segment, .ui.tabular.menu + .attached:not(.top).segment + .attached:not(.top).segment {
    background: #202020;
}

ui.segments .segment, .ui.segment {
    background: #202020;
   	color: #fff;
}

.ui.header:last-child {
    color: #fff;
}

.ui.table:last-child {
    color: #fff;
}

.ui.tabular.menu .item {
    color: #fff;
}

.ui.grid > .column:not(.row), .ui.grid > .row > .column {
    color: #fff;
}

.ui.text.menu {
    background: #dedede;
    border-radius: 5;
    border: 0;
}

.ui.link.menu .item:hover, .ui.menu .dropdown.item:hover, .ui.menu .link.item:hover, .ui.menu a.item:hover {
    cursor: pointer;
    background: #fff;
    color: #202020;
}

.ui.header {
    color: #fff;
}

h1.ui.header .sub.header {
    color: #fff;
}

.ui.items > .item > .content > a.header {
    color: #fff;
}

button, select {
    text-transform: none;
    color: #000;
}

.ui.table thead th {
    color: #fff;
}
}

@-moz-document url("https://platinumcheats.net/") {
/*Place Holder*/
}
