css:
@-moz-document domain("superdramatv.com") {

/* 放送時間の縦制限解放 */
.detail-schedule {
	max-height: none;
    overflow-y: auto;
}

/* 放送時間の余白削減 Padding 20px -> 3px */
.detail-schedule__item {
	padding: 3px;
}

/* 放送時間の一行化 block -> inline */
ul > li.detail-schedule__item > div,
ul > li.detail-schedule__item > div > p.onair-date,
p.lineup-description {
	display: inline;
}

/* 放送時間の余白調整 */
p.lineup-description > span.inline-block {
	    padding: 0 0 0 20px;
}

}
