css:
@-moz-document domain("raisingthedead.ninja") {
html {
	background-color: #333;
}
article p {
	font-size: 1.3em;
	letter-spacing: .06em;
	line-height: 2em;
	text-rendering: optimizelegibility;
	font-family: "Bitstream Charter", "Libre Baskerville", serif;
	color: #8a755c;
}
}

@-moz-document domain("raisingthedead.ninja") {
html {
	filter: hue-rotate(180deg) invert(96%) !important;
}
img,
video {
	filter: brightness(85%) invert(96%) hue-rotate(180deg) !important;
}
}
