css:
@-moz-document domain("etjump.com"), url-prefix("https://etjump.com") {
a, a:link, a:visited {
    color:#000 !important;
}

header a, header a:link, header a:visited {
    border:0;
    font-weight:bold;
    font-size:14px;
}

header a:hover {
    background-color:#a3ddba;
}

main a:hover {
	color:#073 !important;
}

header > p:first-of-type::after {
    content:" \"Actively\".";
}
}
