css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gimp.org") {
   #menu {
     margin: 0 !important;
     padding: 0 !important;
     float: none !important;
     width: 100% !important;
     text-align: center !important;
   }
   #menu > a {
      display: inline !important;
      margin: 1px !important;
   }
   #menu > a > img {
      margin: 0.25em !important;
   }
   #main > div.splash {
      right: 0px !important;
   }
  #main > p.intropara {
      right: 0px !important;
   }
}
