@-moz-document domain("workflowy.com")
{

/* # copy (⿻) */

span.contentTag[title="Filter #copy"] span {
    display: none;
}
span.contentTag[title="Filter #copy"]::after {
    content: "⿻";
    display: inline !important;
    visibility: visible !important;
    color: black !important;
    opacity: .4 !important;
}

span.contentTag[title="Filter #copy"]:hover:after {
    opacity: 1 !important;
}

span.contentTag[title="Filter #copy"] {
    visibility: hidden;
    /*     display:none; */
}

