css:
@-moz-document regexp("^.*novel44.blogspot\\..*") {
body {background: none !important; background-color: #2a2a2a !important; color: #ccc}

.content {background-color: #2a2a2a !important;}
.content-inner  {background-color: #2a2a2a !important;}
#navbar {background-color: #2a2a2a !important;}

a:link {color:#f0f0f0 !important;}
a:visited {color:#606060 !important;}
a:hover {color:#ffffff !important;}
a:active {color:#ffffff !important;}
h3.post-title.entry-title {color:#f0f0f0 !important;}
 
#blog-pager.blog-pager { background-color:transparent !important; }
a#Blog1_blog-pager-older-link.blog-pager-older-link, 
a#Blog1_blog-pager-newer-link.blog-pager-newer-link, 
a.home-link { background-color: #2a2a2a !important; }

div.widget > h2, div.widget h2.title {color: #ccc}

.date-header span {color: #ccc}
.tabs-inner a {background-color: #2a2a2a;}
.tabs-inner .section:first-child ul {background: #2a2a2a;}
.tabs-inner .widget li a:hover {background-color: #4a4a4a;}

.post-footer {background-color: #4a4a4a; color: #ccc}
.post-footer abbr {color: #fff;}

.widget {background-color: #2a2a2a;}
}
