css:
@-moz-document url-prefix("http://www.eecg.toronto.edu/~enright/teaching/ece243S/notes/") {
*{
    color:			#444;
}

body{
    background:		#eee;
    max-width:		70%;
    margin:			auto;
    margin-bottom:	100px;
    margin-top:		100px;
}

p, p span{
    font-size:		20 !important;
    line-height:	1.25;
    text-align:		justify;
    font-family:	serif !important;
}

div>p:first-of-type, div>p:nth-of-type(2), div>p:nth-of-type(3), div>p:nth-of-type(4){
    font-size:		32 !important;
    line-height:	1.25;
    text-align:		justify;
    font-family:	sans-serif !important;
}

div>p>b:only-child, div>p>b:only-child span{
    font-size:		28 !important;
    line-height:	1.25;
    text-align:		justify;
    font-family:	sans-serif !important;
    border-top:		20px;
    border-bottom:	20px;
}



h1{
    font-size:		24 !important;
}
}
