css:
@-moz-document domain("ilimas.ru") {
div.posts-box,
div.site-content {
    background-color: #b3b3b3 !important;
}
body.home.blog {
    background-color: #757575 !important;
}
.site,
html,
body.post-template-default.single.single-post.postid-13753.single-format-standard,
border-top,
body {
    background-color: #120f12 !important;
}
}

@-moz-document url-prefix("http://ilimas.ru/serial") {
header.site-header {
    display: none;
}
h1.entry-title.post-title {
    display: none;
}
div.top-line.js-top-line.show {
    display: none;
}
aside.widget-area {
    display: none;
}
div.related-articles.posts-box {
    display: none;
}
footer.site-footer {
    display: none;
}
div.entry-content p {
    position: absolute;
    font-size: 0;
    top: 150px;
    left: 200px;
}
.entry-content img {
    position: relative;
    left: -265px;
}
}
