    background-color: transparent !important;
}

/* The font-color of the inbox-label should be black. I like it black. */
.n0 {
    color: #000000 !important;
}

/* Since we got rid of the whole boxy thingy, we need to have something better. How about normal style? */
.n0:hover,
.CK:hover,
.CJ:Hover {
    color: #d54e41 !important;
}
.nZ .nU,
.nZ .n0 {
    color: #d54e41 !important;
}

