.bAw {
    display: none !important;
}

/* The top banner is a bit to high: */

/* First, we lower the distance to the mail-container and the top of the screen */
.gb_Ed {
    padding: 4px !important;
}

/* Then, we make the search-bar a little bit smaller. Nifty. */
.gb_of {
    height: 32px !important;
}
.gb_Qd {
    height: 32px !important;
}
.gb_Cc {
    height: 32px !important;
}

