    height: 20px !important;
}

/* Get rid of the annoying border-radius on displayed labels */
.ar,
.hU.hM,
.hV.hM {
    border-radius: 0 !important;
}

/* Cascade the mails for better visibility */
.zA:nth-child(2n) {
    background: #ffffff !important;
}
.zA:hover {
    background: #e5e5e5 !important
}
}
