css:
@-moz-document domain(reddit.com) {
/*Fixes the width of the popup comments/thread to make it fullscreen*/
#lightbox, .dEkasW, .hFQbTk, .iAdiYV, .jAKvrM, .eQjckQ, .bAegjo, .jCFgvD, .hJMyvk, .foTlBI, .cjSZnS, .chcjWD, .jglMBI, .gmUPwM, .bhPpjH{
    width: 100%;
    max-width: 100%;
}
/*Maximises the threads in the larger thread mode*/
.bYauEr, .gbBZCK, .s1x90c2c-0, .hFVyNU{width: 100%;}
/*Moves the thread size option buttons to the left*/
.blTIRL, .jKWqlr{left: 24px;}
/*Moves the posts text to the left*/
.jMWHDn, .gASEKe, .hfrWAZ, .kbDzDr{max-width: 100%; height: 100%; margin: auto; position: relative; padding: 0px 27px;}
/*Moves the subreddit image to the left*/
.kbDzDr{margin: auto;}
/*Changes the colour of the links back to the original blue*/
.eiGEiW{color: rgb(0, 0, 255);}
.knpfRQ{color: rgb(0, 0, 255);}
.dShkqA{color: rgb(0, 0, 255);}
}
