css:
@-moz-document url-prefix("https://mail.google.com") {
* {
	font-family: Roboto,RobotoDraft,Helvetica,'Noto Sans Arabic', Arial,sans-serif
}
.z0 {
	margin: 0 0 16px;
	padding: 0 0 0 24px;
}
.z0 > .L3 {
	background-color: #db493c;
	background-image: none;
	-moz-border-radius: 0;
	border-radius: 0;
	color: #fff;
	display: -webkit-box;
	display: -webkit-flex;
	display: -ms-flexbox;
	display: flex;
	margin-left: -24px;
	padding: 0 24px 0 10px;
}
.z0 > .L3::before {
	filter: sepia(100%) brightness(174%);
}
.z0 > .L3:hover, .z0 > .L3:focus {
	background-color: #c23a2b;
}
.bhZ:not(.bym) .z0 > .L3 {
	padding: 0 24px 0 10px;
}
}
