css:
@-moz-document url-prefix("http://tfs") {
/* The default font is hard to read when colors are inverted. Therefore use Arial.. */
* {
    font-family: "Arial";
}
/* and increase the letter spacing. */
.tbTileContent, .rendered-markdown {    
    letter-spacing: 0.5px;
}
/* invert all colors */
.main-container, .ui-dialog, .file-contents-container {
    filter: invert(100%);
}

/* undo for specific elements */
.identity-picture, .external-content-iframe, img, .dark-widget, .bar, .icon, .piechart-chartsurface, .legend-color-container, .work-item-color, .button, .grid-cell-bgfill, .message-area, .tbTile, .tbTileContent, .header-colorbar, .deleted-content, .added-content {
    filter: invert(100%);
}

/* Fix header style */
#header-row, .legacy-header {
    background: #e5e5e5 !important;
    color: #040404 !important;
}
/* use gray font instead of white for better readabillity on black background. */
.header-item, .text, .product-name, .bowtie-icon, .slash, .grid-row, .clickable-title, .info-text, input, textarea {
    color: #6c6c6c !important;
}
div.bowtie-icon { 
    color: #fff !important;
}
div.bowtie-triangle-down {
    color: #3b3b3b !important;
}
.menu-item.hover {
    background-color: #bfbfbf !important;
}
}
