@media (min-width: 850px) {
    .tabs-bar {
    	display: flex;
        width: 830px;
        margin: 10px auto 1px auto;
    }
    
    .columns-area {
    	width: 850px;
        margin: auto auto auto auto;
    }
    
    .drawer__header {
    	display: none;	
    }
    
    .column {
    	display: none;
    }
    
    .column-header__wrapper {
    	display: none;
    }
    
    .column:last-child {
    	display: flex;
        width: 550px;
    }
}
