css:
@-moz-document domain(eljur.ru) {
table.eljur-table.table--colored-lines>tbody>tr:not(.hide):nth-child(odd), table.eljur-table.table--colored-lines>div  {
	background-color: #222 !important;
}
table.eljur-table.table--colored-lines>tbody>tr:not(.hide):nth-child(2n), table.eljur-table.table--colored-lines>div  {
	background-color: #000;
}
.layout-header {padding-top: 0px;}

div, .diary-header__fixed, .table_header_fixed, .layout-header, td, .layout, body {
	color: #f5f5f5 !important;
	background: #000 !important;
	background-color: #000 !important;
	background-image: none !important;
}
div.navigation-main, nofollow, hightlight {
	background-image: -webkit-gradient(linear,left top,left bottom,from(#000),to(#222222eb)) !important;
}
div#yandex_ad_right, div#quest3, div#quest1 {
	display: none;
}
a.layout-banner {
	display: none !important;
}
}
