css:
/* i really want this to be global */

main .orchest-view {
    background-color: #222;
    color: white;
}
main .orchest-view a {
    color: white;
    font-weight: bold;
}

main .panel-default, main .panel-success, main .panel-danger, main .panel-warning {
	border: 0 !important;
    border-radius: 4px !important;
    -overflow: hidden;
    -webkit-box-shadow: 3px 3px 5px 0px rgba(0, 0, 0, 0.75);
	-moz-box-shadow: 3px 3px 5px 0px rgba(0, 0, 0, 0.75);
	box-shadow: 3px 3px 5px 0px rgba(0, 0, 0, 0.75);
}
main .panel-default > .panel-heading, main .panel-default > .panel-footer {
	background-color: #555;
    border: 0;
    color: white;
}
main .panel-default > .panel-body, main .panel-default > .panel-default, main .panel-body {
	background-color: #777;
    border: 0 !important;
    color: white;
}

main .btn-default, main a.btn-default, .main panel-success a, main .panel-danger a, main .panel-warning a, .dropdown-menu a {
    color: #222 !important;
    font-weight: normal !important;
}

main .branches-table .table {
    border: 0;
}
main .theader {
    background-color: #666;
}
main .table-row:nth-child(2n+1) {
    background-color: #888;
}
main .table-row:nth-child(2n) {
    background-color: #999 !important;
}
main .table-header, .table-small {
    border: 0 !important;
}

main .branches-table .panel-default, .branches-table .panel-default .panel-heading {
    border: 0 !important;
    box-shadow: none;
    margin: 0 !important;
    border-radius: 0 !important;
    background-color: #555;
}
main .branches-table .panel-default {
    border-top: 1px solid #222 !important;
}
main .branches-table a {
    color: white;
    font-weight: bold;
}

.text-muted {
    color: #9A9A9A;
}

main .tab-deploy-type:hover {
    color: #222;
}

.pagination li a {
    color: #666;
}
