@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('http(s)\:\/\/(.*)redmine.*'), url-prefix('https://redmine.') {
  
/* státusz */

tr.status-1 > td.status {background-color: /*#fcc*/#ff4040 !important; } /* new */
tr.status-2  > td.status{ background: #cdf !important; } /* assigned */
tr.status-3 > td.status { background: #33df99 !important; } /* Megoldva */
tr.status-4 > td.status { background: #afd !important; } /* feedback */
tr.status-5 > td.status { background: #dfd !important; } /* closed */
tr.status-6 > td.status { background: #fd6 !important; } /* rejected */
tr.status-10 > td.status { background: #f0d6 !important; } /* rejected */
tr.status-11 > td.status { background: #ac77dc !important; } /* tesztelésre kiadva */
tr.status-26 > td.status { background: #ac77dc !important; } /* tesztelésre kiadva */
tr.status-7  > td.status{ background: #ff4040 !important; } /* reopen */

/* tracker */
tr.tracker-27 > td.tracker { background: #30ee30 !important; } /* item */
tr.tracker-28 > td.tracker { background: #505099 !important; } /* task */
tr.tracker-31 > td.tracker { background: #ee3030 !important; } /* bug */
}
