css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('http(s)\:\/\/(.*)redmine(.*)') {
  /* prioritás */

tr.priority-high2> td.priority { background: #3030ff !important; color:#ffffff !important;} /* Magas */
tr.priority-5> td.priority { background: #3030ff !important; color:#ffffff !important;} /* Magas */

tr.priority-highest> td.priority { background: #ff3030 !important; color:#ffffff !important;} /* Sürgős */
tr.priority-6> td.priority { background: #ff3030 !important; color:#ffffff !important;} /* Sürgős */

tr.priority-lowest> td.priority { background: #40dd40 !important; } /* Alacsony */
tr.priority-3> td.priority { background: #40dd40 !important; } /* Alacsony */
}
