css:
@-moz-document regexp('http(s)://plexstorm\\.com/stream/(.*)')
{
body {
      overflow-y: hidden;
  }
  .settings {
      display: none;
  }
  .streamer {
      position: absolute;
      left: 0;
      width: 75% !important;
      height: 100%;
      top: 0;
      z-index: 100;
      background: #000;
  }
  .chat-column {
      position: absolute;
      right: 0;
      width: 25% !important;
      height: 100%;
      top: 0;
      min-width: 300px;
      z-index: 100;
  }
  .chat {
      height: 100% !important;
      background: #271b3d;
      max-height: calc(100% - 118px);
  }
  .ranking > .h2.mb-8{
  	 display: none;
  }
  .ranking {
                     height: 134px;
                        overflow-y: auto;
                        background: #271b3d;
  }
  .ranking-wrapper.mb-4 {
                        padding-top: 20px;
                    }
  .chat-column .ranking {
    margin-top: 0rem !important;
  }
  .video-wrapper,
  .stream-wrapper,
  .video-container,
  .video-container *,
  #player_html5_api_html5_api {
      height: 100% !important;
  }
  .vjs-control-bar {
      height: 30px !important;
  }
  .video-js .vjs-volume-panel .vjs-volume-control.vjs-volume-horizontal {
      height: 2px !important;
  }
  .video-js .vjs-volume-level {
      bottom: auto;
  }
  .vjs-slider-horizontal .vjs-volume-level:before {
      top: -.4em;
  }
	
  video {
  	max-height: 100% !important;
  }

  footer {
      display: none;
  }
}
