css:
@-moz-document domain(twitter.com)
{
.module ul:last-child li:last-child .list-link, .module ul:last-child li.last-item .list-link, .module .list-link.last, .module .flex-module:last-child {
    visibility: hidden;
}

.module {
	visibility: hidden;
}

.dashboard {
    float: left;
    width: 290px;
    visibility: hidden;
}

html, body, #doc, #page-outer {
    height: 100%;
    background-color: white;
}

.stream-item:not(.no-header-background-module) {
    background: #fff;
    border-left: 1px solid #e6ecf000;
    border-right: 1px solid #e6ecf000;
    background-clip: padding-box;
}

}
