css:
@-moz-document domain(rpg-maker.fr) {

/* largeur max */
body {
    max-width: 1300px;
}

/* nettoyage accueil */
table.tableaccdroite:nth-of-type(2),
table.tableaccdroite:nth-of-type(3),
table.tableaccdroite:nth-of-type(4),
table.tableaccdroite:nth-of-type(5) {
    display: none;
}

/* nettoyage accueil */
td#fondgauchemid p:nth-child(n+2),
td#fondgauchemid div {
    display: none;
}

/* img de posts n'élargissent plus les pages */
td.postsmilieu img {
    max-width: 100%;
}

/* suppression des signatures */
table.tableposts tbody tr:nth-of-type(3) {
    display: none;
}
}
