css:
/* i really want this to be global */
img, img.transparent{
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    /*image*/
    background: url(https://i.imgur.com/o8GduKt.gif) center no-repeat;
    /*image adj*/
    background-size: cover;
    width: inherit;
    height: inherit;
    padding-left: 100%;
}
body {
    background: url(https://i.imgur.com/uTWdyZg.gif) center fixed !important;
    background-size: cover !important;
}

@-moz-document domain("schoolido.lu") {
.home-wrapper,
#carousel-index .item {
    background: url(https://i.imgur.com/uTWdyZg.gif) center fixed !important;
    background-size: cover !important;
}
.non_idolized,
.idolized {
    display: unset;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    /*image*/
    background: url(https://i.schoolido.lu/c/250Nico.png) center no-repeat;
    /*image adj*/
    background-size: cover;
    width: 50%;
    height: 450px;
    padding-left: 100%;
}
#cuteform-modal img.cuteform-elt {
    background: url(https://i.schoolido.lu/chibi/small_Nico_Summer.png) center repeat;
    height: 80px;
    padding-left: 100%;
    width: 80px;
    border-radius: 0;
}
.talking-character {
    background: url(https://i.schoolido.lu/cards/transparent/909Transparent.png) center no-repeat !important;
}
.twitter-text:after {
    content: "fun is infinite with Nico";
}
}

@-moz-document domain("discordapp.com") {
.avatar-large,
.avatar-small,
.image-33JSyf,
.friends-icon {
    background: url(https://i.imgur.com/uTWdyZg.gif) center no-repeat !important;
    background-size: cover;
}
}

@-moz-document domain("google.com") {
.big #logocont {
    display: none;
}
}
