css:
@-moz-document domain("novel18.syosetu.com"), domain("ncode.syosetu.com") {
.novel_title, .novel_writername, #novel_ex, .subtitle, .novel_subtitle, .novel_view, .contents1 {
    font-family: "游明朝", "Yu Mincho", YuMincho, self;
    font-weight: normal;
}
}

@-moz-document url-prefix("https://syosetu.com/usernoveldatamanage/top/ncode/"), url-prefix("https://syosetu.com/usernoveldatamanage/updateinput/ncode/"), url-prefix("https://syosetu.com/userwrittingnovel/input/"), url-prefix("https://syosetu.com/userwrittingnovelmanage/top/wno/"), url-prefix("https://syosetu.com/userwrittingnovelmanage/updateinput/wno/"), url-prefix("https://syosetu.com/novelpreview/top/from/"), url-prefix("https://syosetu.com/usernoveldatamanage/updateconfirm/ncode/"), url-prefix("https://syosetu.com/usernovelmanage/top/ncode/"), url-prefix("https://syosetu.com/usernovelmanage/ziwaconfirm/ncode/") {
.novelview, #novelcontribute_confirm dd, .novel, div.part textarea {
    font-family: "游明朝", "Yu Mincho", YuMincho, self;
    font-weight: normal;
    font-size:16px;
    word-break: normal;
    word-wrap: break-word;
}
.novelview > p, #novelcontribute_confirm dd p, div.writtingnovel p, .view
{
    word-break: normal;
    word-wrap: break-word;
}
#title, #novel, .title, .view, #novel_title {
    font-family: "游明朝", "Yu Mincho", YuMincho, self;
    font-weight: normal;
}

}
