css:
/* i really want this to be global */

.l-nav,
.l-page-side,
.c-block-card__meta,
.c-post-preview__lead,
.c-block__footer,
.c-preview-pics,
.c-post-preview__title,
.c-post-preview__info > span:last-child,
.o-anchor {
    display: none;
}
.c-block-card__body {
    padding: 0;
}
.c-block-card__body:after {
    display: none;
}
.g-column-mid {
    width: 100%;
}
.g-column-mid + * {
    display: none;
}
.c-block {
    display: inline-block;
    margin: 0;
    clear: none;
    background-color: transparent;
    border-bottom: 0;
}
.c-block-card {
    display: block;
}
.c-pager--alt {
    margin: 20px 0;
}
.c-post-preview__info {
    margin: 5px 0;
}
