@-moz-document domain("www.linux.org.ru") {
/* Fix panel */
     
#hd {position: fixed !important;
     display: block !important;
     font-size: 1em !important;
    /* border: 1px dotted red !important; */
     width: 99% !important;
     padding-top: 0em !important;
     margin: 0 !important;
     background-color: #2E3436 !important;
     z-index: 10 !important;
} 
 
#bd {padding-top: 2em !important;}
     

div.menu, div.menu > ul {
     display: inline-block !important;
     font-size: 0.99em !important;
     font-style: normal !important;
     float: right !important;
     padding: 0 !important;
     margin: 0 !important;
     line-height: 1em !important;}

#sitetitle, .loginGreating {display: inline-block !important; font-size: 1em !important; margin: 0 !important; padding-top: 0.1em !important;}
#sitetitle {margin-top: 0 !important;}
.loginGreating {float: left !important;}

/* Move tags to the right */
span.tag {float: right !important;}
     
/* Message list table style */
/* table.message-table {border-radius: 1em !important; border-collapse:  initial !important;}
table.message-table>thead {border-radius: 1em !important; border-collapse:  initial !important;  border: solid red 1px;} */
/* table.message-table {border-spacing: 0px !important; border: 1px solid #808080 !important;} */
table.message-table>tbody>tr:nth-child(odd) {background-color: #2E3436 !important;}
table.message-table>tbody>tr:nth-child(even) {background-color: #1E2426 !important;}
table.message-table>tbody>tr:hover {background-color: #3E4446 !important;}
table.message-table>tbody>tr>td {border: 0 !important;}

}
