css:
@-moz-document domain("www.baidu.com") {
.hint_right_middle,.opr-recommends-panel,.c-gap-bottom opr-toplist-title,.opr-recommends-merge-panel,.cr-title,.ad-block,.opr-toplist-info{
    width:0 !important;
    height:0 !important;
    overflow:hidden !important;
}

div[id^="50"],div[id^="40"],div[id^="30"],div[id^="20"],div[id^="10"],div[id^="60"],div[id^="70"]{
    opacity:0;
}

.result {
    margin-bottom: 0 !important;
    border-bottom: 1px solid rgba(192, 195, 195, 0.18) !important;
    padding: 5px 0 !important;
}
.c-abstract {
    line-height: 30px !important;
}
.f13>a:nth-child(4),
.f13>span,
#s_upfunc_menus,
.res_top_banner{
    display: none !important
}
a>.c-showurl {
    color: green !important;
}
/**search-item*/

#su{
  font-family: "Microsoft Yahei" !important;   
}

#u_sp {
    margin-right: 30px !important;
}
#s_tab {
    border-bottom: #e0e0e0 1px solid !important;
    padding-bottom: 8px !important;
}
#s_tab a,
#s_tab b {
    width: 59px !important;
    line-height: 44px !important;
}
#s_tab b {
    border-bottom: 3px #4879BD solid !important;
}
/**search-banner*/

.s_form {
    top: 45%;
    position: relative;
}
#s_upfunc_menus {
    width: 90%;
}
.s-weather-wrapper {
    float: right;
}
.s-icons,
#s_wrap,
.s-sp-menu>a,
#content_right{
    display: none !important;
}
/** index-page */

*{  font-family: "consolas","SimSun" !important;}
a,
em {
    text-decoration: none !important;
}
a:hover,
a:active {
    color: #FF0000 !important;
}
a[data-click],
em {
    font-family: arial;
}
