css:
@-moz-document domain("subnettingpractice.com") {
/* Author: noized */

/* Main Styles */
body {
    background-color: #222 !important;
}
#nav {
    background-color: #8a8a8a;
}
a {
    color: #608dff !important;
}
ul {
    background-color: #222 !important;
}
li a:hover {
    background-color: #737373;
}
*,
span,
p,
h1,
small {
    color: #fff !important;
    text-align: center;
}


/* Input Styles */
button,
input {
    background-color: #5f5858 !important;
    color: #fff !important;
    
}
input::placeholder {
    color: #b7b7b7 !important;
}


/* Hide Bottom Text */
#question_number+ p,
p:nth-child(14),
p:nth-child(15){
    display: none;
}


/* Image Style */
#myCanvas {
    background-color: #847c7c !important;
    display: block;
    margin-left: auto;
    margin-right: auto;
}


/* Show Answer Style */
#solution {
    background-color: #243c4a !important;
}


/* Table Styles */
tr{
    background-color: #847c7c !important;
    display: block;
    margin-left: auto;
    margin-right: auto;
}
table{
    width:70%; 
    margin-left:15%; 
    margin-right:15%;
}


/* IPV6 Subnet Calculator Styles */
#slashes{
    background-color: #847c7c !important;
}
#facts span{
    background-color: #243c4a !important;
}

    
}
