css:
@-moz-document url-prefix("https://app.roll20.net/campaigns/"), url-prefix("https://marketplace.roll20.net/"), url-prefix("https://roll20.net/compendium/"), url-prefix("https://app.roll20.net/forum/"), url-prefix("https://app.roll20.net/private_message/inbox/"), url-prefix("https://app.roll20.net/account/"), url-prefix("https://app.roll20.net/users/"), url-prefix("https://app.roll20.net/playerdirectory/"), url-prefix("https://roll20.net"), url-prefix("https://app.roll20.net/sessions/"), url-prefix("https://app.roll20.net/lfg/"), url-prefix("https://app.roll20.net/forum"), url-prefix("http://blog.roll20.net/"), url-prefix("https://app.roll20.net/vault/") {
.container, .topbar .menu-hider, .topbar .btn-group, body.loggedin .container {background-color: #0e0e0e
}

body, label, p {color: #555
}

h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, .chosen-container-multi .chosen-choices li.search-field input[type="text"] {color: #656565
}

body, body.loggedin{background-color: #0a0a0a
}

.row, .posts .postcontent, .posts .post .redactor_box {background-color: #0e0e0e !important
}

.redactor_editor, .redactor_editor:focus, input, #mytags, .chosen-container-multi .chosen-choices, .chosen-container .chosen-drop {background: #b3b3b3 !important
}

.btn-default, .well.gray, .breadcrumb, a.btn, .topbar .btn-group.open>a, .topbar .btn-group.open .dropdown-toggle, .features, .forum h3 {background-color: #1d1d1d
}

.topbar .full, .topbar .simple {-webkit-text-fill-color: #555;
               background-color: #1d1d1d
}

.topbar .logo img.withoutad {Background-color: #252525;
                             Border-radius: 10px
}

h2, .col-md-4 div {background-color: #1d1d1d !important
}

.btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .topbar .btn:active {background-color: #313131
}

.faded-container {background: linear-gradient(to bottom,#0a0a0a 63%,#1d1d1d 100%)
}

.table-striped>tbody>tr:nth-child(odd)>td, .table-striped>tbody>tr:nth-child(odd)>th {background-color: #252525
}

a.userprofile {color: #888
}

.topwell {background: linear-gradient(to bottom,#1d1d1d 63%,#313131 100%)
}

.redactor_editor h1, .redactor_editor h2, .redactor_editor h3, .redactor_editor h4, .redactor_editor h5, .redactor_editor h6 {color: #717171
}

.pagination>li>a, .pagination>li>span {border: 1px solid #505050;
                                       Background-color: #313131;
}


}
