css:
@-moz-document domain("riot.im") {
.mx_RoomView_messageListWrapper, .mx_MessageComposer_wrapper, .mx_TopUnreadMessagesBar, .mx_RoomView_auxPanel, .mx_MessageComposer_formatbar, .mx_RoomView_statusAreaBox {
	max-width: none;
}
.mx_MemberList form {
	display: flex;
	justify-content: center;
}
.mx_MatrixChat .mx_MatrixChat_middlePanel {
	padding: 0;
}
.mx_RoomHeader_wrapper {
	max-width: 95%;
}
.mx_MemberList {
	width: 100%;
}
.mx_EntityTile_avatar, .mx_GroupRoomTile_avatar {
	padding-left: 10px;
}
.mx_RightPanel_header {
	width: 100%;
	display: flex;
	justify-content: center;
}
.mx_RightPanel_headerButtonGroup {
	width: 90% !important;
}
.mx_MessageComposer_videocall {
	margin-right: 10px;
}
.mx_RightPanel_footer {
	margin-right: 0;
	display: flex;
	justify-content: center;
}
.mx_RightPanel_invite.mx_AccessibleButton {
	width: 90% !important;
}
.mx_MImageBody {
	max-width: 800px;
}
.mx_RoomSubList_label {
	padding: 6px;
	padding-left: 30px;
	width: auto;
}
.mx_RoomSubList_chevron {
	right: 0;
	left: 10px;
}
.mx_RoomView_statusAreaBox_line {
	margin-left: 0;
	width: 100%;
}
}
