css:
@-moz-document domain("play.pokemonshowdown.com"), domain("smogtours.psim.us"), domain("psim.us")
{

  .greentext
  {
    color: black!important;
  }

  .dark span.greentext
  {
    color: #DDD!important;
  }

  li[id$='-userlist-user-ladychatnoir'] strong, li[id$='-userlist-user-ladychatnoir'] span, .chat.chatmessage-ladychatnoir span, li[id$='-userlist-user-ladychatnoir'] em[style], .chat.chatmessage-ladychatnoir.mine strong, .username-ladychatnoir span
  {
    color: #FF4071!important;
    font-weight: bold;
    font-style: italic;
    border: 1px solid #FF4071;
    border-radius: 5px
  }

 li[id$='-userlist-user-zenithial'] strong, li[id$='-userlist-user-zenithial'] span, .chat.chatmessage-zenithial span, li[id$='-userlist-user-zenithial'] em[style], .chat.chatmessage-zenithial.mine strong
  {
    color: #9A16A6!important;
    text-decoration: underline!important;
  }

 li[id$='-userlist-user-soakage'] strong, li[id$='-userlist-user-soakage'] span, .chat.chatmessage-soakage span, li[id$='-userlist-user-soakage'] em[style], .chat.chatmessage-soakage.mine strong
  {
    color: #90E5FF!important;
  }
  .dark em span.username
  {
    color: DDD!important;
  }

  .spoiler
  {
    background: none;
    color: black;
    -webkit-filter: blur(3px);
    filter: blur(2px);
  }

  .spoiler:hover
  {
    background: none;
    -webkit-filter: none;
    filter: none;
  }

  .infobox-limited
  {
    max-height: 1000px !important;
    overflow: auto;
    overflow-x: hidden;
  }
  .img.logo
{ 
   width: 500px;
   height: 50px;
   }

}
