css:
@-moz-document url-prefix("https://tastedive.com/") {
/*General background*/
body {
	
	background-color: black !important;
	
}

/*Containters titles , info with more information , usersname, feed-everybody- background*/
.tk-Resource, .tk-Alert--info, .tk-User, .tk-Feed-item, .row.tk-Tear-usersRow, .tk-Tear-links {
	
	background: #121212 !important;
	
}

/*Textcolor: title and description background*/
.tk-Tear-teaser, .tk-Tear-title, .tk-Tear-teaser a, .tk-Tear-link {

	color: #900b0b !important;

}
/*More background*/
.tk-Resources-more {
	
	background: #2d3e50 !important;
	
}

/*Open container background*/
.tk-Tear, .tk-Tear-titleColumn.col-md-6, .tk-Tear-actionsColumn.col-md-6 {
	
	background: #2c2c2c !important;
	
}

/*Little square when containers are open*/
.tk-Tear-pointer {
	
	background: #200707 !important;
	
}

/*Title of the containers: the movie; the game, etc.*/
.tk-Resource-title, .tk-Tear-title, .tk-Tear-teaser a {
    
    color: #900b0b;
    
}

.tk-Tear-teaser, .tk-Tear-title, .tk-Tear-teaser a, .tk-Tear-link {
    
    color: #97971b !important;
    
}
}

@-moz-document url("https://tastedive.com/gimailito") {
/*profile header from white to black*/
.tk-Profile-header, .tk-Profile-underHeader.row {
	
    background-color: #000000 !important;
	
}
}
