css:
@-moz-document domain("app.nuclino.com") {
body, div {
    background-color: #333 !important;
	color: #ddd;
}

.import-dropzone div {
    color: #aaa !important;
}

/* title area */
div.ProseMirror {
    color: #ffc36e !important;
}

.ProseMirror p {
    color: #bbb !important;
}

.ProseMirror strong {
    color: #ddd !important;
}

.ProseMirror a {
    color: #b39b78 !important;
}

.ProseMirror code {
    background-color: #515445 !important;
    color: #ddd;
}

h1, h2, h3, h4, h5, h6  {
    color: #ffc36e !important;
    font-weight: 600;
    text-shadow: none !important;
}

::selection {
    background-color: #ddd;
}

.ProseMirror h1:hover,
.ProseMirror h2:hover,
.ProseMirror h3:hover,
.ProseMirror h4:hover,
.ProseMirror h5:hover,
.ProseMirror h6:hover,
.ProseMirror p:hover {
    background-color:#222 !important;
}
}
