css:
@-moz-document domain(kibe.la) {

/* Adjoining class. Ignoring IE6 */
.markdown-body.is-markdown-body-kibela > h1 {
    padding-left: 0.2ex;
    border-left: solid 6pt #e6e6e6;
    border-bottom: solid 2pt #e6e6e6;
}
.markdown-body.is-markdown-body-kibela > h2 {
    border-bottom: solid 2pt #e6e6e6;
}
.markdown-body.is-markdown-body-kibela > h3 {
    border-bottom: dotted 2pt #e6e6e6;
}
.markdown-body.is-markdown-body-kibela > h4 {
    border-bottom: dashed 2pt #e6e6e6;
}
.markdown-body.is-markdown-body-kibela > h5 {
    border-bottom: dashed 1pt #e6e6e6;
}

}
