css:
@-moz-document domain("app.nuclino.com") {
body, p {
    font-family: "AppleSDGothicNeoM00" !important;
}

.ProseMirror p {
    font-size: 88%;
    line-height: 1.2rem;
    margin-bottom: 6px;
}

.ProseMirror p strong {
    font-weight: bold;
}


.ProseMirror a {
    font-size: 93%;
    color: #165a94 !important;
}

.ProseMirror code {
    font-size: 85%;
    font-family: "Ubuntu Mono";
}

h1, h2, h3, h4, h5, h6  {
    color: #165a94 !important;
    font-weight: 600;
    text-shadow: none !important;   
}
}
