css:
/* Userstyle by The_Sink */
/* https://www.roblox.com/users/57188090/profile */

@-moz-document domain(roblox.com) {


.StatusView .bottom {
    background: none;
}
.StatusView .bottom .content {
    left: auto;
}
  
  
.GroupPanelContainer .StatusView div.top {
    background: none;
    background-color: rgb(240,240,240);
    border: 1px solid #9e9e9e;
    border-radius: 5px;
}

.GroupListImageContainer img {
    background: none;
    border: 0px;
}

.GroupListItemContainer.selected {
    background: #ffffff;
    box-shadow: 1px 1px 10px #afafaf;
}

.GroupListImageContainer {
    padding-top: 8px;
}

.GroupListName {
    height: auto;
    padding: 20px 6px;
}  

.btn-primary, .btn-primary:link, .btn-primary:active, .btn-primary:visited {
    background-image: none!important;
    border-radius: 6px;
    box-shadow: 0 2px 20px rgba(25,25,25,0.35);
    background-color: #428bca;
    border-color: transparent;
}

.btn-large.btn-negative {
    background-image: none!important;
    border-radius: 6px;
    box-shadow: 0 2px 20px rgba(25,25,25,0.35);
    background-color: #5e5e5e;
    border-color: transparent;
}
 
.rbx-nav-sponsor, #upgrade-now-button {
  display: none; /* begone thot */
}
.font-bold.small {
  display: none; /* cease */
}
  
.btn-control, .group-search-button { 
  box-shadow: 0 2px 10px rgba(25,25,25,0.2);
  border-radius: 5px;
  }
.gear-button { 
  box-shadow: 0 2px 10px rgba(25,25,25,0.2);
  border-radius: 3px;
  }

#build-dropdown-menu {
  border-radius: 5px;
}
  
  .rbx-vip-server-create {
   margin-top: 7px;
   margin-right: 7px;
  }

.GroupControlsBox {
padding-bottom: 10px;
}
a.place-active {
background-image: url(https://i.imgur.com/tz1vlIQ.png);
}
.rbx-left-col.nav-show {
	box-shadow: 0 3px 6px 0 rgba(0, 0, 0, 0.175), 0 4px 20px 0 rgba(0, 0, 0, 0.175);
}
.GroupPlace img {
border-radius: 8px;
}
}
