css:
@-moz-document domain("telegram.org") {
.im_page_split {
	position: relative
}
.im_dialogs_col_wrap {
	background-color: white;
	left: 0;
	min-width: 82px;
	position: absolute;
	top: 0;
	transition: .5s;
	z-index: 3;
	width: 82px;
}
.im_dialogs_col_wrap > * {
	opacity: .33;
}
.im_dialogs_col_wrap:hover > * {
	opacity: 1;
}
.im_dialogs_col_wrap .im_dialog_date {
	display: none;
}
.im_dialogs_col_wrap:hover .im_dialog_date {
	display: initial;
}
.im_dialogs_col_wrap .pull-right {
	position: absolute;
	right: 5px;
	top: -3px;
}
.im_dialogs_col_wrap:hover .pull-right {
	position: initial;
	right: initial;
	top: initial;
}
.im_dialogs_col_wrap:hover {
	border-right: 1px solid lightgrey;
	min-width: 320px;
	transition: 0s;
	opacity: 1;
	width: 42%;
}
.im_dialogs_col_wrap {
	transition: .2s;
}
.im_dialogs_col_wrap:hover + .im_history_col_wrap {
	opacity: .2;
}
.im_history_col_wrap {
	margin-left: 14%;
	width: 100%;
}
.im_message_wrap,
.im_bottom_panel_wrap {
	margin: 0;
	max-width: 78.5%;
}
.im_send_panel_wrap {
	border-top: 1px solid whitesmoke;
}

@media screen and (max-width: 600px) {
	.icon-tg-title {
		display: none;
	}
}
}
