css:
@-moz-document domain(l-lsoc.cf) {
#left-menu {
display: block;
position: fixed;
margin: 50px 0px 0px 0px;
}
#button {
border-top-color: #0064C7;
border-bottom-color: #0064C7;
border-left-color: #0064C7;
    border-right-color: #0064C7;
    background: url('http://egor21312.ru/button.png');
}
#content-infoname {
    background: #DAE1E7;
    color: #446493;
    border: 0.5px solid #DAE1E7;
}
#head-wrapper {
    background: url('http://egor21312.ru/modern.png');
    background-color: #0064C7;
}
#head-button {
    background: url('http://www.1x1px.me/FFFFFF-0.0.png');
    background-color: #0064C7;
    border-radius: 0%;
}
#head {
    background-color: #0064C7;
display: block;
position: fixed;
        box-shadow: 0px 1px 3px 0px;
}
#avatar {
    object-fit: cover;
    width: 50px;
    height: 50px;
    border-radius: 15% !important;
    overflow: hidden;
}
img {
    border-radius: 0%;
}
#content-wall-post-avatar {
    object-fit: cover;
    width: 50px;
    height: 50px;
    border-radius: 15% !important;
    overflow: hidden;
}
body {
    border-radius: 0%;
}
#content-main {
margin: 50px 0px 25px 0px;
    box-shadow: 0px 2px 5px 0px;
}

#content-wall-post {
    box-shadow: 0px 0px 0px 2px rgba(218, 225, 231, 1.0);
}
}
