css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bash.org.ru") {
* {color: #404040 !important}
body { width: 80% !important; margin: .2cm auto !important; 
background: #fff !important;}
#page { margin: auto !important; width: auto !important; 
text-align: center !important;}
.menu { margin: 0 !important; color: #000 !important; padding: 
5px !important; font-family: Arial, sans-serif !important; 
text-align: left !important; border-bottom: 1px #ddd dashed 
!important;}

div#quotes > div.q:first-child > div.vote {display: none 
!important;}

DIV.q > DIV INPUT + INPUT {position: relative !important; top: 
-1px !important;}
.aby {border: none !important; background: transparent 
!important;}
.aby > a:hover {background: #ddd !important;}
.news.stats, .header, div#bar, hr { display: none !important;}
div.menu a:link, div.menu a:hover, div.menu a:visited, a:link, 
a:hover, a:visited {color: #555 !important;}
div#quotes + div.menu { border-bottom: 0 !important; 
background-color: white !important; text-align: left 
!important;  margin-bottom: 35px !important; }
div.news { font-family: 'Arial', 'Tahoma', sans-serif 
!important; }
.b b { font-weight:  normal !important; }
small { font-family: 'Times New Roman', serif !important; }
input {width: auto !important}
div#quotes > div.q { font-family: sans-serif 
!important; font-size: 100% !important; }
div#quotes > div.q > div { display: none !important; 
background: white !important; padding: 0 !important; border: 
none !important;}
div#quotes > div.q > div.vote { display: block !important; 
margin-top: 10px !important; color: #777 !important; 
padding-top: 3px !important; font-family: inherit !important;}
div#quotes > div.q > div.vote + div { display: block 
!important; border: none !important; border-bottom: 1px #ddd 
dashed !important; padding-bottom: 3px !important;}
.pager {display: block !important; position: absolute 
!important; }


.header {display:none !important;}
}

@-moz-document url(http://bash.org.ru/abysstop) {
div#quotes > div.q:first-child > div.vote,
div#quotes > div.q:first-child > div.vote + div { position: 
static !important; display: block !important;}
}
