css:
@-moz-document url-prefix("https://www.gg.pl/"){
* {
    font-family: Ubuntu !important
}

body {
    background-color: #2D2D2D
}

.sr-contact {
    color: #fafafa !important;
    background: none !important;
}

sr-contact-name {
    color: #fafafa !important;
    background: none !important;
    background-color: #2d2d2d;
}

chat-toolbar {
    background-color: #252525;
}

.share-links-widget {
    color: #FAFAFA
}

.sr-user-name {
    color: #fafafa
}

.sr-contact:hover {
    color: #8bc34a !important;
    background: #333333 !important;
}

.sr-contact:hover .sr-contact-desc {
    color: #ccc !important;
}

.chat-accordion-item.expanded .opened-item {
    background-color: #252525;
    border: 1px solid #444;
}

.header .title {
    text-shadow: none !important;
    color: #Fafafa !important;
}

.opened-item .description {
    text-shadow: none !important;
}

.sr-contact .sr-contact-name {
    color: #fafafa;
}

.you {
    background-color: #1D1F21 !important;
}

.me {
    background-color: #2D2D2D !important;
}

.item-body-text {
    color: #fafafa !important;
}

.item-showname {
    color: #fafafa !important;
}

.chat-accordion-item .body {
    background-color: #2d2d2d !important;
}

.chat-submit-toolbar {
    background-color: #252525;
}

.sr-message-area #chat-text {
    color: #fafafa;
    background-color: #2d2d2d;
}

#profile-description-description .description-big {
    color: #fff;
}

.user-data-top .show-name {
    color: #fff;
}

.sr-notifications-header {
    color: #fff;
}

.sr-notifications-header-tabs LI A {
    color: #fafafa;
}

.user-data-bottom>SPAN {
	    font-size: 23px;
}
.roulette-container p.aditionalInfo {
	    font-size: 20px;
}
}
