css:
@-moz-document url-prefix("https://userstyles.org") {
/*general background*/

body, div#nav-view, div#navigation, div#footer, div#top_android_button, div#overlay-info {
	
	background-color: black !important;
	
}

/*background text area*/

.notes_textarea {
	
    background-color: #0c0e12;
	
}

/*buttons: stats, etc.*/
.white_button.no-select {

	background-color: black !important;
	border: none;
	
}

/*install button*/
div#install_style_button 
	
/*title of the style thumbs*/
.style_card, .style_card.overrideHref {
	
	background-color: #141212;
	
}

/*Color of navigation title; the news, home, etc.*/
.navigation_title {
    
    color: #55708a !important;
	
}

/*color of container in Sites*/
.style_category_card_wrapper {
    
    background-color: #120f0f !important;
	
}
}
