css:
@-moz-document url-prefix("https://pikabu.ru/") {
aside.sidebar__inner:before {
    content: "посмотреть";
    color:#fff;
    text-align: center;
    line-height: 10;
       text-transform: uppercase;
    display: block;
    width: 300px;
    height: 140px;
    left: 0;
    top: 0;
    background-color: #333;
    position: absolute;
    z-index: 2;
        opacity: 1;
    transition:all .2s ease-in-out;
}

aside.sidebar__inner:hover:before{
    z-index: -1;
    opacity: 0;
}

}
