css:
@-moz-document domain("steamcommunity.com") {

.playerAvatar.online {
    background: -webkit-linear-gradient(top, #53A4C4 0, #458097 100%);
    background: linear-gradient(to bottom, #53A4C4 0, #458097 100%);
}
.playerAvatar.in-game {
    background: -webkit-linear-gradient(top, #8FB93B 0, #6E8C31 100%);
    background: linear-gradient(to bottom, #8FB93B 0, #6E8C31 100%);
}
.playerAvatar.offline {
    background: -webkit-linear-gradient(top, #959595 0, #333 100%);
    background: linear-gradient(to bottom, #959595 0, #333 100%);
}

/*Improved borders for avatars*/
.playerAvatar {
    padding: 3px !important;
    border-radius: 2px !important;
}

/*Fixes borders for profile avatar*/
.profile_header .playerAvatarAutoSizeInner {
    margin: 3px !important;
    border-radius: 2px !important;
}

/*Re-align the profile summary a little bit.*/
.profile_header .playerAvatar.profile_header_size {
    margin-right: 14px;
}

}
