css:
@-moz-document domain("youtube.com") {
  .ysm-collection-switcher-item {
    opacity: 0.5;
    font-size: 80%;
    font-weight: 600;
    font-family: "Roboto";
    border: 0 !important;
    padding: 2px;
  }
  .ysm-collection-switcher-item img {
    display: none;
  }
  .ysm-collection-footer {
    opacity: 0.5;
    margin-top: 5px;
  }
  .ysm-collection-switcher-item-main {
    float: left !important;
    padding-left: 0 !important;
    margin: 0 0 0 2px;
    opacity: 0.4;
  }
  .ysm-collection-tristate-switcher > input[type="radio"] + i::before {
    width: 12px;
    height: 12px;
    top: -2px;
  }
  /* bar */
  
  .ysm-collection-tristate-switcher > input[type="radio"] + i {
    height: 8px;
    width: 20px;
    margin-top: 5px;
    margin-right: 2px;
  }
  /* little dot when idle */
  
  .ysm-collection-tristate-switcher > input[type="radio"]:checked + input[type="radio"] + i::before {
    left: 4px;
  }
  /* little dot when checked */
  
  .ysm-collection-tristate-switcher > input[type="radio"]:checked + i:before {
    left: 9px;
  }
  .ysm-subscription-filter {
    border: 0 !important;
    margin-bottom: 0 !important;
    padding-bottom: 0 !important;
  }
  .ysm-subscription-filter .branded-page-module-title-text {
    color: transparent !important;
  }
  .ysm-collection-premium {
    display: none;
  }
  
  .ysm-show-filters-btn {
  	margin-bottom: -5px;
    margin-top: 5px;
    font-size: 85%;
  }
}
